import React, {Component} from 'react';
import Layout from './components/Layout/Layout';
import TodoAdder from './containers/TodoAdder/TodoAdder';
import TodoItem from './components/TodoItem/TodoItem';

class App extends Component {
  state = {
      value: '',
      itemValue: ''
  }

  inputHandler = (event) => {
      this.setState({
          value: event.target.value
      });
  };

  handleAddItem = (event) => {
      this.setState({
        itemValue: this.state.value,
        value: ''
      });
      console.log('Item name saved: ' + this.state.itemValue);
      event.preventDefault();
  }

  showItemValue = () => {
    return <TodoItem name={this.state.itemValue}/>;
  }

  render(){
    return(
      <div>
        <Layout>
          <TodoAdder 
            submitProp={this.handleAddItem} 
            onChange={this.inputHandler} 
            value={this.state.value}/>
          <TodoItem name={this.showItemValue()}/>
        </Layout>
      </div>
    );
  }
}

export default App;
