import React, {Component} from 'react';

const TodoAdder = () => {
    return(
        <form onSubmit={this.handleAddItem}>
            <input value={this.state.value} onChange={this.inputHandler}/>
            <input type='submit' value='Add' />
        </form>
    );
}

export default TodoAdder;
