import React, {Component} from 'react';
import Layout from './components/Layout/Layout';
import TodoAdder from './containers/TodoAdder/TodoAdder';
import TodoItem from './components/TodoItem/TodoItem';

class App extends Component {
  state = {
      value: '',
      items: []
  }

  inputHandler = (event) => {
      this.setState({
          value: event.target.value
      });
  };

  handleAddItem = (event) => {
    this.state.items.push({name: this.state.value})
      this.setState({
        value: ''
      });
      console.log('Item name saved: ' + this.state.value);
      event.preventDefault();
  }

  deleteItemHandler = (itemIndex) => {
    const items = [...this.state.items];
    items.splice(itemIndex, 1);
    this.setState({items: items});
  }


  render(){
    return(
      <div>
        <Layout>
          <TodoAdder 
            submitProp={this.handleAddItem} 
            onChange={this.inputHandler} 
            value={this.state.value}/>

          {this.state.items.map((item, index) => {
            return <TodoItem 
              name={item.name}
              deleteItem={() => this.deleteItemHandler(index)}/>;
          })}

        </Layout>
      </div>
    );
  }
}

export default App;
