import React, {Component} from 'react';
import Layout from './components/Layout/Layout';
import TodoAdder from './containers/TodoAdder/TodoAdder';
import TodoItem from './components/TodoItem/TodoItem';

class App extends Component {
  state = {
      value: '',
      items: []
  }

  makeid = (length) => {
    let result           = '';
    let characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let charactersLength = characters.length;
    for ( let i = 0; i < length; i++ ) {
       result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
  }

  isEmptyOrSpaces = (str) => {
    return str === null || str.match(/^ *$/) !== null;
  }

  inputHandler = (event) => {
      this.setState({
          value: event.target.value
      });
  };

  handleAddItem = (event) => {
    if(this.isEmptyOrSpaces(this.state.value)){
      //do nothing
    }else{
      this.state.items.push({name: this.state.value, id: `${this.makeid(5)}`});
        this.setState({
          value: ''
        });
    }
      event.preventDefault();
  }

  deleteItemHandler = (itemIndex) => {
    const items = [...this.state.items];
    items.splice(itemIndex, 1);
    this.setState({items: items});
  }

  modifyItemHandler = (itemIndex, event) => {
    const items = [...this.state.items];
    const item = {...items[itemIndex]};
    item.name = event.target.value;
    items[itemIndex] = item;
    this.setState({items});
  }

  render(){
    return(
      <div>
        <Layout>
          <TodoAdder 
            submitProp={this.handleAddItem} 
            onChange={this.inputHandler} 
            value={this.state.value}/>

          {this.state.items.map((item, index) => {
            return <TodoItem 
              name={item.name}
              deleteItem={() => this.deleteItemHandler(index)}
              modifyItem={(e) => this.modifyItemHandler(index, e)}
              key={item.id}/>;
          })}

        </Layout>
      </div>
    );
  }
}

export default App;
